mapper.select:
        select
id, username, phone, email, salt, password, age, state, department_id, shop_id
        from t_employee


                and id = #{id}


                and username = #{username}


                and phone = #{phone}


                and email = #{email}


                and salt = #{salt}


                and password = #{password}


                and age = #{age}


                and state = #{state}


                and department_id = #{departmentId}


                and shop_id = #{shopId}


        limit #{pageable.offset}, #{pageable.pageSize}
