mapper.select:
select count(1)
from t_pet


        and id = #{id}


        and name = #{name}


        and costprice = #{costprice}


        and saleprice = #{saleprice}


        and type_id = #{typeId}


        and resources = #{resources}


        and state = #{state}


        and offsaletime = #{offsaletime}


        and onsaletime = #{onsaletime}


        and createtime = #{createtime}


        and shop_id = #{shopId}


        and user_id = #{userId}


        and search_master_msg_id = #{searchMasterMsgId}
