mapper.select:
select
id, sn, name, dir_path, state, manager_id, parent_id
from t_department


        and id = #{id}


        and sn = #{sn}


        and name = #{name}


        and dir_path = #{dirPath}


        and state = #{state}


        and manager_id = #{managerId}


        and parent_id = #{parentId}


limit #{pageable.offset}, #{pageable.pageSize}

SELECT EXISTS (
SELECT 1 FROM t_department WHERE parent_id IN
#{id}
UNION ALL
SELECT 1 FROM t_employee WHERE department_id IN
#{id}
)
